// wap to print 1 to 20 odd numbers
package basic_programs.java;

public class OddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		for(i=1; i<=20; i++){
			if(i%2==1){
				System.out.println(i+ " Odd numbers: ");
			}
		
		}
	}

}
